import { useEffect, useState } from "react"
import { toggleUserRoom } from "../backend/fetchData"

interface RoomToggleProps {
    roomName: string
    isSignedIn: boolean
    userId: number
}
function RoomToggle({ roomName, isSignedIn, userId }: RoomToggleProps) {
    const [isOn, setIsOn] = useState<boolean>(isSignedIn)

    useEffect(() => {
        setIsOn(isSignedIn)
    }, [isSignedIn])

    const clickToggle = () => {
        setIsOn((prevIsOn) => {
            const newIsOn = !prevIsOn
            toggleUserRoom(userId, roomName)
            return newIsOn
          })
    }

    return (
        <div className={isOn ? "toggleOn" : "toggleOff"} onClick={clickToggle} />
  )
}

export default RoomToggle
