import { User, Children } from "../../classes"

// let db = new sql.Database("d:\CareCentral.db", (err) => {
//         if (err) {
//             return console.error(err.message)
//         }
//         console.log('Connected to Care Central')
//     })

interface UserRow {
    userId: number;
    username: string;
    // This will be hashed
    passwordHash: string;
    role: string;
    profileImage: Blob | null;
}

const url: string = "http://127.0.0.1:8000/"

export async function createUser(username: string): Promise<User> {
    return new Promise((resolve, reject) => {
        fetch(url + "users/" + username)
            .then(response => response.json())
            .then(json => {
                let newUser = new User(json.username)
                if (json.role) {
                    newUser.setRole(json.role)
                }
                if (json.children) {
                    json.children.forEach((child: Children) => {
                        newUser.setChild(child)
                    });
                }
            })
    })
}
