import { useState, useEffect } from 'react';
import logo from '../../images/logo.png';
import dropdown from '../../images/sort-down.png'
import '../../style/App.css';
import SwitchList from './SwitchList';
import UserInfo from './UserInfo';
import ProfileDropdown from './ProfileDropdown';
import { User } from '../../classes';
import { createUser } from '../backend/fetchData';

function App() {
  // Replace with login
  const [username, setUsername] = useState<string>("Gina")

  const [user, setUser] = useState<User | null>()
  const [showProfileDropdown, setProfileDropdown] = useState<boolean>(false)

  const defineUser = () => {
    if (username) {
      console.log("Username defined")
    } else {
      return false
    }
    if (user) {
      console.log("User data defined")
      return true
    } else {
      createUser(username)
        .then((userData) => {
          setUser(userData)
        })
        .then(() => {
          if (user) {
            console.log("User data defined")
            return true
          } else {
            defineUser()
          }
      })
    }
  }

  useEffect(() => {
    defineUser()
  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <img className='logo' src={logo} alt='' />
        <nav>
          <li>Home<img src={dropdown} alt=''/></li>
          <li>Dashboard<img src={dropdown} alt='' /></li>
          <li onClick={() => { setProfileDropdown(prevState => !prevState) }}>
            <UserInfo user={user} />
            <img src={dropdown} alt='' />
          </li>
        </nav>
      </header>
      {showProfileDropdown ? <ProfileDropdown username={username}/> : null}
      <div className='mid-container'>
        {user?.getRole() === 'family' ? <SwitchList username={username} /> : null}
      </div>
    </div>
  );
}

export default App;
