import { useEffect, useState } from "react"

interface ToggleProps {
  id: number
  initialIsOn: boolean,
  changeData: (id: number) => void
}

function Toggle({ id, initialIsOn, changeData }: ToggleProps) {
  const [isOn, setIsOn] = useState<boolean>(initialIsOn)

  useEffect(() => {
    changeData(id) 
  }, [isOn])
  

  const clickToggle = () => {
    setIsOn((prevIsOn) => !prevIsOn)
  }
  
  return (
    <div className='toggle-container' onClick={clickToggle}>
      <div className={isOn ? "toggle-on" : "toggle-off"} />
    </div>
  )
}

export default Toggle
